mapper.insert:
insert into gps_points
(car_number, lng, lat, `time`, tra_id)
values

    (
    #{point.carNumber}, #{point.lng}, #{point.lat}, #{point.time}, #{point.traId}
    )

mapper.select:
select *
from gps_trajectory_stat

    car_number = #{carNumber}

        and start_time >= #{minTime}


        and start_time <= #{maxTime}


order by start_time

select car_number as carNumber, count(distinct DATE_FORMAT(start_time,'%Y-%m-%d')) as dayCount, sum(distance_cal) as totalDistance, avg(distance_cal) as avgDistance, count(*) as traCount, sum(timestampdiff(second, start_time, end_time)) / 3600 as totalTime, avg(timestampdiff(second, start_time, end_time)) / 3600 as avgTime, totalDistance / totalTime as avgSpeed, totalDistance / dayCount as avgDistancePerDay, totalTime / dayCount as avgTimePerDay from gps_trajectory_stat
and start_time >= #{param.minTime}
and start_time <= #{param.maxTime}
group by car_number having traCount >= 10

select count(distinct car_number) as carNum,
       count(*) as traNum
from gps_trajectory_stat


        and start_time >= #{minTime}


        and start_time <= #{maxTime}

select car_number as carNumber,
       lng,
       lat,
       `time`,
       tra_id as traId
from gps_points

    car_number = #{carNumber}

        and `time` >= #{minTime}


        and `time` <= #{maxTime}


order by `time`

select *
from camtrajectory_taxi

    car_number = #{carNumber}
    and cam_lat is not null
    and cam_lon is not null

        and photo_time >= #{minTime}


        and photo_time <= #{maxTime}

select car_number as carNumber, lng, lat, `time`, tra_id as traId from gps_points
and `time` >= #{minTime}
and `time` <= #{maxTime}
order by `time`

select start_lng as lng,
       start_lat as lat,
       count(*) as `count`
from gps_trajectory_stat
where end_lng between #{minLng} and #{maxLng}
and end_lat between #{minLat} and #{maxLat}
and start_time >= #{minTime} and end_time <= #{maxTime}
group by start_lng, start_lat

select end_lng as lng,
       end_lat as lat,
       count(*) as `count`
from gps_trajectory_stat
where start_lng between #{minLng} and #{maxLng}
  and start_lat between #{minLat} and #{maxLat}
  and start_time >= #{minTime} and end_time <= #{maxTime}
group by end_lng, end_lat
